var GLOBAL_ITEMS = function(){
	var items = [];
	for(var key in this) items.push(key);
	return items;
}();

(function(){
    this.Logger = {};
    this.Logger.id = false;
    this.Logger.key = false;
    this.Logger.token = false;
    this.Logger.verbose = false;
    this.Logger.color = true;
    this.Logger.colors = {
        id : 'yellow',
        type : 'blue'
    };
    this.Logger.typeColor = {
        'Error' : 'red',
        'error' : 'red',
        'Request' : 'magenta',
    };
    this.Logger.log = function(text, type){
        if(!type) type = 'output';
        console.log(
            (Logger.key?Logger.wrap('k:'+Logger.key, Logger.colors['id']):'')+
            (Logger.token?Logger.wrap('t:'+Logger.token, Logger.colors['id']):'')+
            (Logger.id?Logger.wrap('r:'+Logger.id, Logger.colors['id']):'')+
            Logger.wrap(type, (Logger.typeColor[type]?Logger.typeColor[type]:Logger.colors['type']))+
            text
        );
    };
    this.Logger.wrap = function(text, color){
        if(Logger.color) return '['+AsciiArt.ansiCodes(text, color)+']';
        return '['+text+']';
    }
})();

if (typeof exports != 'undefined') (function(){
    for (var key in this) if (!GLOBAL_ITEMS.contains(key)){
        exports[key] = this[key];
    }
    exports.apply = function(object){
        Object.append(object, exports);
    };
})();

